import React from 'react'
import { Container, Form, FormGroup, Button } from 'react-bootstrap'
import './App.css'

export default function Login() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [errorMessage, setErrorMessage] = useState('')
  const navigate = useNavigate()

  const handleSubmit = (e) => {
    e.preventDefault()
    setErrorMessage("")
    // Call API to login
    login(email, password)
      .then(res => {
        if (res.status === 200) {
          if (res.data.status) {
            localStorage.setItem('userId', res.data.user.uid);
            getProfileByUserId(res.data.user.uid)
              .then(res => {
                localStorage.setItem('username', res.data.user.firstName + ' ' + res.data.user.lastName)
                navigate('/');
              })
          }
          else {
            setErrorMessage(res.data.message)
          }
        }
        else {
          setErrorMessage('Đăng nhập thất bại')
        }
      })
      .catch(err => {
        console.log(err)
        setErrorMessage('Lỗi không xác định')
      })
  }

  return (
    <div className='signup-login'>
      <Container className='container shadow border p-4 pb-0 rounded-2' style={{width: '450px', marginTop: '30px',color: 'white'}}>
        <Form className='d-flex flex-column'>
          <h1 className='align-self-center' style={{color:"black"}} >Đăng nhập</h1>
          <FormGroup className='mb-2'>
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" placeholder='Email' />
          </FormGroup>
          <FormGroup className='mb-3 position-relative'>
            <Form.Label>Mật khẩu</Form.Label>
            <Form.Control type="password" placeholder='Mật khẩu' />
          </FormGroup>
          <Button className='signup-login-button align-self-center mb-2' type='submit' style={{width: '150px', color:"black"}}>Đăng nhập</Button>
          <p className='align-self-center' style={{color:"black"}}>Chưa có tài khoản?</p>
        </Form>
      </Container>
    </div>
  )
}
