import React, { useState } from 'react'
import { Container, Form, FormGroup, Button } from 'react-bootstrap'
import { Link, useNavigate } from 'react-router-dom'
import { register } from './services/API';
import { toast } from 'react-toastify';



export default function SignUp() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrorMessage("");
    if (password !== confirmPassword) {
      setErrorMessage("Mật khẩu không khớp");
      return;
    }
    // Call API to register
    register(email, password)
      .then(res => {
        if (res.status === 200) {
          if (res.data.status) {
            toast.success(res.data.message)
            navigate('/setup-account', {state: {user: res.data.user}})
          }
          else {
            toast.error(res.data.message);
          }
        }
        else {
          setErrorMessage("Đăng ký thất bại");
        }
      })
      .catch(err => {
        setErrorMessage("Đăng ký thất bại");
      })
  }
  return (
    <div className='signup-login'>
      <Container className='container shadow border p-4 pb-0 rounded-2' style={{ width: '450px', marginTop: '20px' ,color: 'white'}} >
        <Form className='d-flex flex-column'>
          <h1 className='align-self-center' style={{color:"black"}} >Đăng ký tài khoản</h1>
          <FormGroup className='mb-2'>
            <Form.Label>Email</Form.Label>
            <Form.Control 
                placeholder='Email' 
                required
            />
          </FormGroup>
          <FormGroup className='mb-2 position-relative'>
            <Form.Label>Mật khẩu</Form.Label>
            <Form.Control
              type="password"
              placeholder='Mật khẩu'
              required
            />
          </FormGroup>
          <FormGroup className='mb-3 position-relative'>
            <Form.Label>Xác nhận mật khẩu</Form.Label>
            <Form.Control
              type= "password"
              placeholder='Xác nhận mật khẩu'
              required
            />
          </FormGroup>
          <Button className='signup-login-button align-self-center mb-2' type='submit' style={{ width: '120px',color:"black"}}>Đăng ký</Button>
          <p className='align-self-center' style={{color:"black"}} >Bạn đã có tài khoản?</p>
        </Form>
      </Container>
    </div>
  );
}
