import React, { useState } from "react";
import { Container, Button, Form, Modal, ListGroup, Image } from "react-bootstrap";

export default function Home() {
  const [posts, setPosts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentPost, setCurrentPost] = useState({ id: null, title: "", content: "", image: null });
  const [isEditing, setIsEditing] = useState(false);

  let nextId = 1; // Biến lưu trữ id tự động tăng

  const handleAddPost = () => {
    if (!currentPost.title.trim() || !currentPost.content.trim()) return;

    if (isEditing) {
      // Sửa bài đăng
      setPosts(posts.map(post => (post.id === currentPost.id ? currentPost : post)));
    } else {
      // Thêm bài đăng mới với id tự động tăng
      setPosts([...posts, { id: nextId++, ...currentPost }]);
    }

    setShowModal(false);
    setCurrentPost({ id: null, title: "", content: "", image: null });
    setIsEditing(false);
  };


  const handleEditPost = (post) => {
    setCurrentPost(post);
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDeletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setCurrentPost({ ...currentPost, image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <Container className="shadow border p-4 pb-0 rounded-2" style={{ width: "600px", marginTop: "100px" }}>
      <h3 className="mb-4">Danh sách bài đăng</h3>
      <Button onClick={() => setShowModal(true)} className="mb-3">
        Thêm bài đăng
      </Button>
      <ListGroup>
        {posts.map(post => (
          <ListGroup.Item key={post.id} className="d-flex flex-column align-items-start mb-3">
            {post.image && <Image src={post.image} thumbnail style={{ maxHeight: "150px", marginBottom: "10px" }} />}
            <h5>{post.title}</h5>
            <p>{post.content}</p>
            <div>
              <Button variant="primary" size="sm" onClick={() => handleEditPost(post)} className="me-2">
                Sửa
              </Button>
              <Button variant="danger" size="sm" onClick={() => handleDeletePost(post.id)}>
                Xóa
              </Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Modal thêm/sửa bài đăng */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? "Sửa bài đăng" : "Thêm bài đăng"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group className="mb-3">
            <Form.Label>Tiêu đề</Form.Label>
            <Form.Control
              type="text"
              value={currentPost.title}
              onChange={(e) => setCurrentPost({ ...currentPost, title: e.target.value })}
              placeholder="Nhập tiêu đề bài đăng"
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Nội dung</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={currentPost.content}
              onChange={(e) => setCurrentPost({ ...currentPost, content: e.target.value })}
              placeholder="Nhập nội dung bài đăng"
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Chọn ảnh</Form.Label>
            <Form.Control type="file" accept="image/*" onChange={handleImageUpload} />
          </Form.Group>
          {currentPost.image && (
            <Image src={currentPost.image} thumbnail style={{ maxHeight: "150px", marginTop: "10px" }} />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Hủy
          </Button>
          <Button variant="primary" onClick={handleAddPost}>
            {isEditing ? "Cập nhật" : "Thêm"}
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
