import React, { useState } from 'react';
import Modal from 'react-modal';

// Thiết lập modal
Modal.setAppElement('#root');

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [imageUri, setImageUri] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddPost = () => {
    const newPost = {
      id: Date.now().toString(),
      title,
      content,
      imageUri,
    };
    setPosts([...posts, newPost]);
    resetForm();
  };

  const handleEditPost = (id) => {
    const postToEdit = posts.find(post => post.id === id);
    setTitle(postToEdit.title);
    setContent(postToEdit.content);
    setImageUri(postToEdit.imageUri);
    setEditingId(id);
    setIsModalOpen(true);
  };

  const handleSaveEdit = () => {
    const updatedPosts = posts.map(post =>
      post.id === editingId ? { ...post, title, content, imageUri } : post
    );
    setPosts(updatedPosts);
    resetForm();
    setIsModalOpen(false);
  };

  const handleDeletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  const handleImagePick = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImageUri(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const resetForm = () => {
    setTitle('');
    setContent('');
    setImageUri('');
    setEditingId(null);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Trang Chủ</h1>

      <button style={styles.addButton} onClick={() => setIsModalOpen(true)}>
        Thêm bài post
      </button>

      <Modal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} style={modalStyles}>
        <h2>{editingId ? 'Sửa bài post' : 'Thêm bài post'}</h2>

        <input
          type="text"
          placeholder="Tiêu đề"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={styles.input}
        />
        <textarea
          placeholder="Nội dung"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          style={styles.input}
        />
        <input
          type="file"
          onChange={handleImagePick}
          style={styles.input}
        />
        {imageUri && <img src={imageUri} alt="Preview" style={styles.imagePreview} />}

        <div style={styles.modalActions}>
          {editingId ? (
            <button onClick={handleSaveEdit} style={styles.modalButton}>Lưu thay đổi</button>
          ) : (
            <button onClick={handleAddPost} style={styles.modalButton}>Thêm bài post</button>
          )}
          <button onClick={() => setIsModalOpen(false)} style={styles.modalButton}>Đóng</button>
        </div>
      </Modal>

      <ul style={styles.postList}>
        {posts.map((post) => (
          <li key={post.id} style={styles.postCard}>
            <h2>{post.title}</h2>
            <p>{post.content}</p>
            {post.imageUri && <img src={post.imageUri} alt="Post" style={styles.postImage} />}
            <div style={styles.actions}>
              <button onClick={() => handleEditPost(post.id)} style={styles.postButton}>Sửa</button>
              <button onClick={() => handleDeletePost(post.id)} style={styles.postButton}>Xóa</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

const modalStyles = {
  content: {
    width: '500px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#fff',
    borderRadius: '10px',
    boxShadow: '0 0 20px rgba(0, 0, 0, 0.1)',
  },
};

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
  },
  header: {
    textAlign: 'center',
    fontSize: '36px',
    color: '#333',
    marginBottom: '20px',
  },
  addButton: {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
    display: 'block',
    margin: '0 auto 20px',
  },
  input: {
    width: '100%',
    padding: '12px',
    marginBottom: '12px',
    borderRadius: '5px',
    border: '1px solid #ddd',
    fontSize: '16px',
  },
  imagePreview: {
    width: '100px',
    height: '100px',
    marginBottom: '12px',
    borderRadius: '8px',
    objectFit: 'cover',
  },
  modalActions: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '20px',
  },
  modalButton: {
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
  },
  postList: {
    listStyleType: 'none',
    paddingLeft: '0',
  },
  postCard: {
    padding: '20px',
    marginBottom: '20px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  },
  postImage: {
    width: '100px',
    height: '100px',
    marginTop: '10px',
    objectFit: 'cover',
    borderRadius: '8px',
  },
  actions: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '20px',
  },
  postButton: {
    padding: '10px 20px',
    backgroundColor: '#f44336',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default Home;
