import React, { useState } from "react";
import { Container, Button, Form, Modal, ListGroup } from "react-bootstrap";

export default function Home() {
  const [posts, setPosts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentPost, setCurrentPost] = useState({ id: null, title: "" });
  const [isEditing, setIsEditing] = useState(false);

  const handleAddPost = () => {
    if (!currentPost.title.trim()) return;

    if (isEditing) {
      setPosts(posts.map(post => post.id === currentPost.id ? currentPost : post));
    } else {
      setPosts([...posts, { id: Date.now(), title: currentPost.title }]);
    }
    setShowModal(false);
    setCurrentPost({ id: null, title: "" });
    setIsEditing(false);
  };

  const handleEditPost = (post) => {
    setCurrentPost(post);
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDeletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  return (
    <Container className="shadow border p-4 pb-0 rounded-2" style={{ width: "500px", marginTop: "200px" }}>
      <h3 className="mb-4">Danh sách bài đăng</h3>
      <Button onClick={() => setShowModal(true)} className="mb-3">
        Thêm bài đăng
      </Button>
      <ListGroup>
        {posts.map(post => (
          <ListGroup.Item key={post.id} className="d-flex justify-content-between align-items-center">
            {post.title}
            <div>
              <Button variant="primary" size="sm" onClick={() => handleEditPost(post)} className="me-2">
                Sửa
              </Button>
              <Button variant="danger" size="sm" onClick={() => handleDeletePost(post.id)}>
                Xóa
              </Button>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Modal thêm/sửa bài đăng */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? "Sửa bài đăng" : "Thêm bài đăng"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Tiêu đề</Form.Label>
            <Form.Control
              type="text"
              value={currentPost.title}
              onChange={(e) => setCurrentPost({ ...currentPost, title: e.target.value })}
              placeholder="Nhập tiêu đề bài đăng"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Hủy
          </Button>
          <Button variant="primary" onClick={handleAddPost}>
            {isEditing ? "Cập nhật" : "Thêm"}
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
