import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [imageUri, setImageUri] = useState('');
  const [editingId, setEditingId] = useState(null);

  const handleAddPost = () => {
    const newPost = {
      id: Date.now().toString(),
      title,
      content,
      imageUri,
    };
    setPosts([...posts, newPost]);
    setTitle('');
    setContent('');
    setImageUri('');
  };

  const handleEditPost = (id) => {
    const postToEdit = posts.find(post => post.id === id);
    setTitle(postToEdit.title);
    setContent(postToEdit.content);
    setImageUri(postToEdit.imageUri);
    setEditingId(id);
  };

  const handleSaveEdit = () => {
    const updatedPosts = posts.map(post =>
      post.id === editingId ? { ...post, title, content, imageUri } : post
    );
    setPosts(updatedPosts);
    setTitle('');
    setContent('');
    setImageUri('');
    setEditingId(null);
  };

  const handleDeletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  const handleImagePick = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImageUri(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div style={styles.container}>
      <h1>Trang Chủ</h1>

      <input
        type="text"
        placeholder="Tiêu đề"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={styles.input}
      />
      <textarea
        placeholder="Nội dung"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        style={styles.input}
      />
      <input
        type="file"
        onChange={handleImagePick}
        style={styles.input}
      />
      {imageUri && <img src={imageUri} alt="Preview" style={styles.imagePreview} />}

      {editingId ? (
        <button onClick={handleSaveEdit}>Lưu thay đổi</button>
      ) : (
        <button onClick={handleAddPost}>Thêm bài post</button>
      )}

      <ul>
        {posts.map((post) => (
          <li key={post.id} style={styles.postCard}>
            <h2>{post.title}</h2>
            <p>{post.content}</p>
            {post.imageUri && <img src={post.imageUri} alt="Post" style={styles.postImage} />}
            <div>
              <button onClick={() => handleEditPost(post.id)}>Sửa</button>
              <button onClick={() => handleDeletePost(post.id)}>Xóa</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#fff',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  },
  imagePreview: {
    width: '100px',
    height: '100px',
    marginBottom: '10px',
  },
  postCard: {
    padding: '10px',
    marginBottom: '10px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  },
  postImage: {
    width: '100px',
    height: '100px',
    marginTop: '10px',
  },
};

export default Home;
